mapper.update:
UPDATE VST_BBS SET BBS_NM = #{bbsNm}, FILE_ATCH_CO = #{fileAtchCo}, FILE_ATCH_SIZE = #{fileAtchSize},
USE_AT = #{useAt},
PERM_EXTSN = #{permExtsn}, LAST_UPDUSR_ID = #{lastUpdusrId}, LAST_UPDT_DT = now() WHERE BBS_ID = #{bbsId}

mapper.select:
SELECT BBS_ID, BBS_NM, USE_AT, DATE_FORMAT(FRST_REGIST_DT, '%Y-%m-%d') FRST_REGIST_DT FROM VST_BBS WHERE 1 = 1
AND(DATE_FORMAT(FRST_REGIST_DT, '%Y-%m-%d') >= #{minDate} AND DATE_FORMAT(FRST_REGIST_DT, '%Y-%m-%d') <= #{maxDate})
AND (BBS_ID LIKE CONCAT('%', #{searchKeyword} , '%') OR BBS_NM LIKE CONCAT('%', #{searchKeyword} , '%'))
ORDER BY
${orderColumn}
BBS_ID
${ascDesc}
DESC
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}
